import React, { Component } from "react";
import "./App.css";

class App extends Component {
  state = {
    currentWord: '',
    currentScore: 0,
    breakdown: [],
    history: [],
    error: null,
    isLoading: false,
    isValidatingWord: false
  };

  LETTER_VALUES = {
    A: 1, E: 1, I: 1, O: 1, U: 1, L: 1, N: 1, R: 1, S: 1, T: 1,
    D: 2, G: 2,
    B: 3, C: 3, M: 3, P: 3,
    F: 4, H: 4, V: 4, W: 4, Y: 4,
    K: 5,
    J: 8, X: 8,
    Q: 10, Z: 10
  };

  calculateScoreLocally = (word) => {
    const cleanWord = word.toUpperCase();
    const breakdown = cleanWord.split('').map(letter => ({
      letter,
      value: this.LETTER_VALUES[letter] || 0
    }));

    const score = breakdown.reduce((sum, item) => sum + item.value, 0);

    return { score, breakdown };
  };

  handleInputChange = (e) => {
    const word = e.target.value.toUpperCase();

    if (word && !/^[A-Z]*$/.test(word)) {
      return;
    }

    const { score, breakdown } = this.calculateScoreLocally(word);

    this.setState({
      currentWord: word,
      currentScore: score,
      breakdown,
      error: null
    });
  };

  handleSubmit = async () => {
    const { currentWord } = this.state;

    if (!currentWord.trim()) {
      this.setState({ error: 'Please enter a word' });
      return;
    }

    this.setState({ isLoading: true, error: null });

    try {
      const response = await fetch('http://localhost:3001/api/scrabble-score', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ word: currentWord })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to calculate score');
      }

      this.setState(prevState => ({
        history: [
          {
            word: data.word,
            score: data.score,
            timestamp: new Date().toLocaleTimeString(),
            id: Date.now()
          },
          ...prevState.history
        ],
        currentWord: '',
        currentScore: 0,
        breakdown: [],
        isLoading: false
      }));
    } catch (error) {
      this.setState({
        error: error.message,
        isLoading: false
      });
    }
  };

  handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      this.handleSubmit();
    } else if (e.key === 'Escape') {
      this.setState({ 
        currentWord: '', 
        currentScore: 0, 
        breakdown: [],
        error: null 
      });
    }
  };

  componentDidMount() {
    document.addEventListener('keydown', this.handleKeyPress);
  }

  componentWillUnmount() {
    document.removeEventListener('keydown', this.handleKeyPress);
  }

  render() {
    const { currentWord, currentScore, breakdown, history, error, isLoading } = this.state;

    return (
      <div className="app">
        <header className="app-header">
          <h1>Scrabble Score Calculator</h1>
        </header>

        <main className="app-main">
          <section className="input-section">
            <div className="tile-rack">
              {currentWord.split('').map((letter, index) => (
                <div key={index} className="tile">
                  <span className="letter">{letter}</span>
                  <span className="points">
                    {breakdown[index]?.value || 0}
                  </span>
                </div>
              ))}
              {currentWord.length === 0 && (
                <span className="placeholder-text">Type a word below...</span>
              )}
            </div>

            <input
              type="text"
              value={currentWord}
              onChange={this.handleInputChange}
              placeholder="Type a word..."
              className="text-input"
              maxLength={15}
              autoFocus
            />

            <div className="score-display">
              <span className="score-label">Score</span>
              <span className="score-value">{currentScore} pts</span>
            </div>

            {error && (
              <div className="error-message">
                {error}
              </div>
            )}

            <button
              className="submit-button"
              onClick={this.handleSubmit}
              disabled={isLoading || !currentWord}
            >
              <span className="icon">âœ“</span>
              <span>{isLoading ? 'Calculating...' : 'Submit Word'}</span>
            </button>
          </section>

          {history.length > 0 && (
            <section className="history-section">
              <div className="history-header">
                <h3>History</h3>
                <span className="count">{history.length} {history.length === 1 ? 'word' : 'words'}</span>
              </div>

              <div className="history-list">
                {history.map(item => (
                  <div key={item.id} className="history-item">
                    <div className="history-content">
                      <div className="history-row">
                        <span className="word">{item.word}</span>
                        <span className="score">{item.score} pts</span>
                      </div>
                      <span className="timestamp">{item.timestamp}</span>
                    </div>
                  </div>
                ))}
              </div>
            </section>
          )}
        </main>
      </div>
    );
  }
}

export default App;
